templategroup.template:
{vb:data node, content_channel, 'getContent', {vb:raw routeInfo.arguments.nodeid}}
{vb:set nodeid, {vb:raw routeInfo.arguments.nodeid}}
{vb:set node, {vb:raw node.{vb:raw nodeid}}}

<h3>{vb:raw node.title}</h3>
{vb:set urlInfo.nodeid, {vb:raw routeInfo.arguments.nodeid}}
{vb:set urlInfo.blogaction, 'admin'}

<div class="blog-admin-content clearfix">
	{vb:template blogadmin_header}

	<div class="blogAdminLeft">
		{vb:template blogadmin_sidebar, nodeid={vb:raw nodeid}, blogaction='admin', thisStep='blog_statistics', blogperms = {vb:raw blogPerms}, routeInfo = {vb:raw routeInfo}}
	</div>

	<div class="blogAdminRight">
		{vb:template widget_blogstats, nodeid={vb:raw nodeid}, page=1}
	</div>
</div>

{vb:data node, node, 'getNode', {vb:raw routeInfo.arguments.nodeid}}
{vb:set nodeid, {vb:raw routeInfo.arguments.nodeid}}
<h3>{vb:raw node.title}</h3>

<div class="blog-admin-content clearfix">

	{vb:template blogadmin_header}

	<div class="blogAdminLeft">
	{vb:template blogadmin_sidebar, nodeid={vb:raw nodeid}, blogaction={vb:raw blogaction}, thisStep='manage_subscribers', blogperms = {vb:raw blogPerms}, routeInfo = {vb:raw routeInfo}}
	</div>

	{vb:data subscribers, blog, 'fetchSubscribers', {vb:raw nodeid}}
	{vb:set pagenav.currentpage, 1}
	{vb:set pagenav.totalpages, {vb:raw subscribers.pagecount}}

	<div class="blogAdminRight">
		<div class="blogAdminEditPage right stretch" data-pagecount="{vb:raw subscribers.pagecount}" data-nodeid="{vb:raw nodeid}">
			<vb:if condition="$subscribers['pagecount'] > 1">
				<div class="stretch right title-bar blogAdminSubscriberPaging" data-pagecount= {vb:raw subscribers.pagecount}>
					{vb:template pagenavnew, pagenav = {vb:raw pagenav}}
				</div>
			</vb:if>
			<div class="manage_moderators_row">
				<div class="moderators_left">
					{vb:phrase blog_subscribers_list}
				</div>
				<div class="moderators_right">
					{vb:phrase action}
				</div>
			</div>

			<div class="subscriberList">
				{vb:template blogadmin_subscriberlist, subscribers={vb:raw subscribers}, nodeid={vb:raw nodeid}}
			</div>
	</div>
</div>

{vb:set urlInfo.nodeid, {vb:raw blog.nodeid}}
{vb:set urlInfo.blogaction, 'admin'}

<vb:if condition="!isset($blogpermissions)">
	{vb:data blogpermissions, blog, getChannelAdminPerms, {vb:raw blog.nodeid}}
</vb:if>

<div class="adminmenu_container">
	<div class="admin-mainmenu clearfix">
		<div class="mainmenu administrative {vb:stylevar left}">
			<span>{vb:phrase administrative}</span>
			<ul class="submenu administrative-submenu">
				{vb:set urlInfo.action2, 'settings'}
				<li class="blogadmin-action"><a href="{vb:url 'blogadmin', {vb:raw urlInfo}}">{vb:phrase general_settings}</a></li>
				{vb:set urlInfo.action2, 'permissions'}
				<li class="blogadmin-action"><a href="{vb:url 'blogadmin', {vb:raw urlInfo}}">{vb:phrase permission_and_privacy}</a></li>

				{vb:set urlInfo.action2, 'contributors'}

				<vb:if condition="$blogpermissions['canadmin']">
					<li class="blogadmin-action"><a href="{vb:url 'blogadmin', {vb:raw urlInfo}}">{vb:phrase manage_contributors}</a></li>
				</vb:if>
				{vb:set urlInfo.action2, 'subscribers'}
				<li class="blogadmin-action"><a href="{vb:url 'blogadmin', {vb:raw urlInfo}}">{vb:phrase manage_subscribers}</a></li>
				{vb:set urlInfo.action2, 'sidebar'}
				<li class="blogadmin-action"><a href="{vb:url 'blogadmin', {vb:raw urlInfo}}">{vb:phrase organize_sidebar}</a></li>
				<vb:if condition="$blogpermissions['canadmin']">
					{vb:set urlInfo.action2, 'stats'}
					<li class="blogadmin-action"><a href="{vb:url 'blogadmin', {vb:raw urlInfo}}">{vb:phrase blog_statistics}</a></li>
				</vb:if>
				{vb:set urlInfo.action2, 'invite'}
				<li class="blogadmin-action"><a href="{vb:url 'blogadmin', {vb:raw urlInfo}}">{vb:phrase invite_members}</a></li>
			</ul>
		</div>
	</div>
</div>

