{vb:set dataLoaded, 0}
	<vb:if condition="!isset($default_tab) OR $default_tab == '#forum-tab'">
		{vb:rawdata forumTabContent, node, fetchChannelNodeTree, -1, 3, 1, 100}

		<vb:if condition="!empty($forumTabContent) AND empty($forumTabContent['errors'])">
			{vb:set channels, {vb:raw forumTabContent.channels}}
			{vb:set root, {vb:raw forumTabContent.root}}
			{vb:set dataLoaded, 1}
		<vb:elseif condition="!empty($forumTabContent) AND !empty($forumTabContent['errors'])" />
			{vb:set channels, {vb:raw forumTabContent}}
		</vb:if>
	</vb:if>

