							<vb:if condition="$pagenum > 1">
								{vb:set urlInfo.action, 'report'}
								{vb:set urlInfo['pagenum'] {vb:raw prevpage}}
								<a id="pmIconMovePrevious" href="{vb:url 'privatemessage', {vb:raw urlInfo}}" class="arrow left-arrow" title="{vb:phrase previous_page}" rel="prev"><span class="vb-icon vb-icon-arrow-left"></span></a>
							<vb:else />
								<a id="pmIconMovePrevious" class="arrow left-arrow disabled" title="{vb:phrase previous_page}" rel="prev"><span class="vb-icon vb-icon-arrow-left"></span></a>
							</vb:if>
							<vb:if condition="$pagenum < $pages">
								{vb:set urlInfo.action, 'report'}
								{vb:set urlInfo.pagenum,  {vb:raw nextpage}}
								<a id="pmIconMoveNext" href="{vb:url 'privatemessage', {vb:raw urlInfo}}" class="arrow right-arrow" title="{vb:phrase next_page}" rel="next"><span class="vb-icon vb-icon-arrow-right"></span></a>
							<vb:else />
								<a id="pmIconMoveNext" class="arrow right-arrow disabled" title="{vb:phrase next_page}" rel="next"><span class="vb-icon vb-icon-arrow-right"></span></a>
							</vb:if>
						<vb:else />
							<vb:if condition="$pagenum < $pages">
								{vb:set urlInfo.action, 'report'}
								{vb:set urlInfo.pagenum,  {vb:raw nextpage}}
								<a id="pmIconMoveNext" href="{vb:url 'privatemessage', {vb:raw urlInfo}}" class="arrow left-arrow" title="{vb:phrase next_page}" rel="next"><span class="vb-icon vb-icon-arrow-left"></span></a>
							<vb:else />
								<a id="pmIconMoveNext" class="arrow left-arrow disabled" title="{vb:phrase next_page}" rel="next"><span class="vb-icon vb-icon-arrow-left"></span></a>
							</vb:if>
							<vb:if condition="$pagenum > 1">
								{vb:set urlInfo.action, 'report'}
								{vb:set urlInfo['pagenum'] {vb:raw prevpage}}
								<a id="pmIconMovePrevious" href="{vb:url 'privatemessage', {vb:raw urlInfo}}" class="arrow right-arrow" title="{vb:phrase previous_page}" rel="prev"><span class="vb-icon vb-icon-arrow-right"></span></a>
							<vb:else />
								<a id="pmIconMovePrevious" class="arrow right-arrow disabled" title="{vb:phrase previous_page}" rel="prev"><span class="vb-icon vb-icon-arrow-right"></span></a>
							</vb:if>
						</vb:if>
					</div>
				</div>
			</vb:if>
		</div>
	</div>
</div>

{vb:hook profile_activity_start}
{vb:data profileInfo, user, fetchProfileInfo, {vb:raw userid}}
<vb:each from="nodes" value="node">
	{vb:set conversation, {vb:raw node.content}}
	{vb:set contenttypeclass, {vb:raw conversation.contenttypeclass}}
	<vb:if condition="$contenttypeclass != 'PrivateMessage'">
		{vb:set showContent, 1}
		<vb:if condition="$contenttypeclass == 'Gallery'">
			{vb:data canViewAlbum, user, hasPermissions, albumpermissions, canviewalbum}
			{vb:data albumChannel, node, fetchAlbumChannel}
			<vb:if condition="isset($canViewAlbum) AND !$canViewAlbum AND ($node['parentid'] == $albumChannel)">
				{vb:set showContent, 0}
			</vb:if>
		</vb:if>
		<vb:if condition="$contenttypeclass == 'Video' AND $profileInfo['showVideos'] == 0">
			{vb:data isFollowing, follow, isFollowingUser, {vb:raw userid}}
			<vb:if condition="isset($isFollowing) AND $isFollowing !== 1">
				{vb:set showContent, 0}
			</vb:if>
		</vb:if>
		<vb:if condition="isset($showContent) AND $showContent > 0">
			{vb:set replyTemplateName, 'display_contenttype_conversationreply_'}
			{vb:strcat replyTemplateName, {vb:raw contenttypeclass}}

			<vb:comment>We wouldn't need this if we had node info on if a node is a blog node or not</vb:comment>
			<vb:if condition="$contenttypeclass != 'Channel'">				
				<vb:if condition="isset($conversation['parents']) AND count($conversation['parents']) > 3">
					{vb:set nodeParents, {vb:raw conversation.parents}}
				</vb:if>

				<vb:if condition="!empty($nodeParents)">		
					<vb:if condition="!isset($mainBlogChannelId)">
						<vb:if condition="!isset($currentNodeIsBlog)">
							{vb:data currentNodeIsBlog, blog, isBlogNode, {vb:raw conversation.nodeid}}
						</vb:if>
						<vb:if condition="$currentNodeIsBlog">
							<vb:if condition="end($nodeParents) AND ($mainBlogChannel = prev($nodeParents))">
								{vb:set mainBlogChannelId, {vb:raw mainBlogChannel.parent}}
							</vb:if>
						</vb:if>
					<vb:elseif condition="end($nodeParents) AND ($mainBlogChannel = prev($nodeParents)) AND $mainBlogChannel['parent'] == $mainBlogChannelId" />
						{vb:set currentNodeIsBlog, 1}
					<vb:else />
						{vb:set currentNodeIsBlog, 0}
					</vb:if>
				<vb:else />
					{vb:data currentNodeIsBlog, blog, isBlogNode, {vb:raw conversation.nodeid}}
				</vb:if>
			</vb:if>

			{vb:template {vb:raw replyTemplateName}, conversation={vb:raw conversation}, postfor={vb:raw node.setfor}, reportActivity = 1, showChannelInfo = 1, showInlineMod={vb:raw showInlineMod}, currentNodeIsBlog={vb:raw currentNodeIsBlog}}
		</vb:if>
	</vb:if>
</vb:each>
{vb:hook profile_activity_end}

{vb:hook following_list_start}
<vb:each from="nodes" value="node">
	{vb:set replyTemplateName, 'display_contenttype_conversationreply_'}
	{vb:strcat replyTemplateName, {vb:raw node.content.contenttypeclass}}

	<vb:if condition="$node['content']['contenttypeclass'] != 'Channel'">
		<vb:if condition="isset($node['content']['parents']) AND count($node['content']['parents']) > 3">
			{vb:set nodeParents, {vb:raw node.content.parents}}
		</vb:if>

		<vb:comment>We wouldn't need this if we had node info on if a node is a blog node or not</vb:comment>
		<vb:if condition="!empty($nodeParents)">		
			<vb:if condition="!isset($mainBlogChannelId)">
				<vb:if condition="!isset($currentNodeIsBlog)">
					{vb:data currentNodeIsBlog, blog, isBlogNode, {vb:raw node.content.nodeid}}
				</vb:if>
				<vb:if condition="$currentNodeIsBlog">
					<vb:if condition="end($nodeParents) AND ($mainBlogChannel = prev($nodeParents))">
						{vb:set mainBlogChannelId, {vb:raw mainBlogChannel.parent}}
					</vb:if>
				</vb:if>
			<vb:elseif condition="end($nodeParents) AND ($mainBlogChannel = prev($nodeParents)) AND $mainBlogChannel['parent'] == $mainBlogChannelId" />
				{vb:set currentNodeIsBlog, 1}
			<vb:else />
				{vb:set currentNodeIsBlog, 0}
			</vb:if>
		<vb:else />
			{vb:data currentNodeIsBlog, blog, isBlogNode, {vb:raw node.content.nodeid}}
		</vb:if>
	</vb:if>

	{vb:template {vb:raw replyTemplateName}, node = {vb:raw node}, conversation={vb:raw node.content}, postfor={vb:raw node.setfor}, reportActivity = 1, showChannelInfo = {vb:raw showChannelInfo}, showSubscribedInfo = 1, currentNodeIsBlog={vb:raw currentNodeIsBlog}}
</vb:each>
{vb:hook following_list_end}

