{vb:set channelid, {vb:raw $page.channelid}}
{vb:rawdata canView, user, hasPermissions, forumpermissions, canview, {vb:raw $channelid}}
{vb:rawdata canViewContent, user, hasPermissions, forumpermissions, canviewthreads, {vb:raw $channelid}}
<vb:if condition="!empty($canView) AND empty($canView['errors']) AND ($canView == true) AND !empty($canViewContent) AND empty($canViewContent['errors']) AND ($canViewContent== true)">
	<vb:if condition="empty($widgetConfig) AND !empty($widgetinstanceid)">
		{vb:data widgetConfig, widget, fetchConfig, {vb:raw widgetinstanceid}}
	</vb:if>
	<vb:if condition="isset($widgetConfig['resultsPerPage']) OR isset($user['maxposts'])">
		<vb:if condition="isset($user['maxposts']) AND $user['maxposts'] != -1">
			{vb:set resultsPerPage, {vb:raw user.maxposts}}
		<vb:else />
			{vb:set resultsPerPage, {vb:raw widgetConfig.resultsPerPage}}
		</vb:if>
	</vb:if>
	{vb:set topicStatus, ''}
	{vb:set errorMessage, 1}
	<vb:if condition="isset($page) AND $page AND isset($page['channelid']) AND $page['channelid'] > 0 AND isset($page['nodeid']) AND $page['nodeid'] > 0">
		{vb:set nodeid, {vb:raw page.nodeid}}
		{vb:data currentConversation, node, getNodeFullContent, {vb:raw nodeid}}
		{vb:data currentNodeIsBlog, blog, isBlogNode, {vb:raw nodeid}}

		<vb:if condition="!$currentNodeIsBlog">
			{vb:data currentNodeIsSG, socialgroup, isSGNode, {vb:raw nodeid}}
		</vb:if>

		<vb:if condition="!empty($currentConversation) AND empty($currentConversation['errors'])">
			{vb:set errorMessage, 0}
			{vb:set currentConversation, {vb:raw currentConversation[$nodeid]}}
			<vb:if condition="$nodeid == $currentConversation['starter']">
				{vb:set conversationStarter, {vb:raw currentConversation}}
			<vb:else />
				{vb:set starternodeid, {vb:raw currentConversation.starter}}
				{vb:data conversationStarter, node, getNodeContent, {vb:raw starternodeid}}
				{vb:set conversationStarter, {vb:raw conversationStarter[$starternodeid]}}
			</vb:if>
			<vb:if condition="!$page['view'] AND $user['threadedmode'] == 2">
				{vb:set page.view, 'stream'}
			</vb:if>

			<vb:if condition="$currentNodeIsBlog">
				{vb:data enableInlineMod, user, hasPermissions, moderatorpermissions, canmoderateposts, {vb:raw page.nodeid}}
			</vb:if>

			<vb:if condition="$page['view'] == 'stream'">
				{vb:set totalCount, {vb:raw conversationStarter.totalcount_1}}
				{vb:set sortOption.created, 'DESC'}
				{vb:set depth, 2}
			<vb:else />
				{vb:set totalCount, {vb:raw conversationStarter.textcount_1}}
				<vb:if condition="$page['view'] != 'thread'">
					{vb:set page.view, 'thread'}
				</vb:if>
				{vb:set sortOption.created, 'ASC'}
				{vb:set depth, 1}
				<vb:if condition="!$currentNodeIsBlog">
					{vb:data enableInlineMod, user, hasPermissions, moderatorpermissions, canmoderateposts, {vb:raw page.nodeid}}
				</vb:if>
				{vb:set options.nolimit, 1}
			</vb:if>

			{vb:set options.sort, {vb:raw sortOption}}
			{vb:set options.include_parent, 1}

			<vb:if condition="!empty($page['p']) AND intval($page['p']) > 0">
				{vb:rawdata nodePageNumber, search, getPageNumber, {vb:raw page.p}, {vb:raw conversationStarter}, {vb:raw resultsPerPage}, {vb:raw depth}, {vb:raw sortOption.created}, 1}
				<vb:if condition="empty($nodePageNumber['errors']) AND $nodePageNumber > 0">
					{vb:set page.pagenum, {vb:raw nodePageNumber}}
				</vb:if>
			</vb:if>
			<vb:if condition="!empty($page['routeInfo']) AND is_array($page['routeInfo'])">
				{vb:data pagingInfo, page, getPagingInfo, {vb:raw page.pagenum}, {vb:raw totalCount}, {vb:raw resultsPerPage}, {vb:raw page.routeInfo}, {vb:raw baseurl}}
				{vb:set currentpage, {vb:raw pagingInfo.currentpage}}
			<vb:else />
				{vb:set currentpage, 1}
			</vb:if>

			<vb:if condition="$currentConversation['showopen'] == 0 AND !$currentConversation['canmoderate'] AND $currentConversation['open'] == 0">
					{vb:strcat topicStatus, ' closed'}
			</vb:if>

			<vb:if condition="($currentConversation['showopen'] == 0) AND ($currentConversation['canmoderate'] == 0)">
				{vb:strcat topicStatus, ' closed_content-entry-box'}
			</vb:if>
			<vb:if condition="$currentConversation['sticky']">
				{vb:strcat topicStatus, ' sticky'}
			</vb:if>
			<vb:if condition="$currentConversation['approved'] == 0">
				{vb:strcat topicStatus, ' unapproved'}
			</vb:if>
			<vb:if condition="$currentConversation['featured']">
				{vb:strcat topicStatus, ' featured'}
			</vb:if>
			<vb:if condition="$currentConversation['unpublishdate']">
				{vb:strcat topicStatus, ' deleted'}
			</vb:if>
			{vb:set options.include_starter, 1}
			{vb:set options.withParent, 1}
			{vb:set options.includeProtected, 1}
			{vb:data nodes, node, listNodeContent, {vb:raw nodeid}, {vb:raw currentpage}, {vb:raw resultsPerPage}, {vb:raw depth}, null, {vb:raw options}}
			{vb:set modPerms, {vb:raw currentConversation.moderatorperms}}
			<vb:each from="nodes" value="node">
				<vb:if condition="!empty($node['content']['moderatorperms'])">
					{vb:set openStatus, {vb:raw node.open}}
					{vb:set content, {vb:raw node.content}}
					{vb:action modPerms, bbcode, actionMergePerms, {vb:raw modPerms}, {vb:raw content.moderatorperms}}
				</vb:if>
			</vb:each>
			<vb:each from="modPerms" value="perm">
				<vb:if condition="$perm > 0">
					{vb:set enableInlineMod, 1}
				</vb:if>
			</vb:each>
		</vb:if>
	</vb:if>

	<vb:if condition="$currentNodeIsBlog">
		{vb:set page.view, 'stream'}
		{vb:set showFilter, 0}
		{vb:set showSearch, 0}
		{vb:data userCanCommentOnThisBlog, blog, userCanComment, {vb:raw currentConversation}}
	<vb:else />
		{vb:set showFilter, 1}
		{vb:set showSearch, 1}
	</vb:if>

	<div class="canvas-widget default-widget conversation-content-widget <vb:if condition="!$currentNodeIsBlog"> forum-conversation-content-widget widget-tabs<vb:else /> blog-conversation-content-widget</vb:if> widget-no-border widget-no-header-buttons {vb:raw topicStatus} axd-container<vb:if condition="$currentNodeIsBlog AND isset($userCanCommentOnThisBlog) AND $userCanCommentOnThisBlog < 1"> no-toolbar</vb:if>" data-widget-id="{vb:raw widgetid}" data-widget-instance-id="{vb:raw widgetinstanceid}"<vb:if condition="!empty($widgetinstanceid)"> data-widget-default-tab="{vb:raw widgetConfig.default_tab}"</vb:if>>
		<vb:if condition="empty($errorMessage)">
			<div class="conversation-status-messages">
				<vb:if condition="$currentConversation['showopen'] == 0">
					<div class="conversation-status-message notice close"><span>{vb:phrase this_topic_is_closed}</span><div class="close">X</div></div>
				</vb:if>
				<vb:if condition="$currentConversation['sticky']">
					<div class="conversation-status-message notice stick"><span>{vb:phrase this_is_a_sticky_topic}</span><div class="close">X</div></div>
				</vb:if>
				<vb:if condition="$enableInlineMod">
					<vb:if condition="$currentConversation['approved'] == 0">
						<div class="conversation-status-message notice approve"><span>{vb:phrase this_topic_has_been_successfully_unapproved}</span><div class="close">X</div></div>
					</vb:if>
					<vb:if condition="$currentConversation['unpublishdate']">
						<div class="conversation-status-message notice delete"><span>{vb:phrase this_topic_is_deleted}</span><div class="close">X</div></div>
					</vb:if>
					<vb:if condition="$currentConversation['featured']">
						<div class="conversation-status-message notice feature"><span>{vb:phrase this_topic_has_been_featured}</span><div class="close">X</div></div>
					</vb:if>
					<div class="conversation-status-message notice hide"><span></span><div class="close">X</div></div>
				</vb:if>
			</div>

			<div class="widget-header clearfix">
				<div class="module-title left main-header-title">
					<vb:if condition="empty($widgetConfig['hide_title'])">
						<span class="main-title hide-on-editmode">{vb:raw currentConversation.title}</span>
					</vb:if>
					<span class="admin-only">{vb:phrase conversation_detail} {vb:phrase module}</span>
				</div>
				<div class="module-buttons moderationmenu_container">
					<vb:if condition="$user['can_use_sitebuilder']">
						<div class="admin-only moderation-mainmenu inline">
							<span class="move-icon module-button-item" title="{vb:phrase move_module}"><span>{vb:phrase move}</span></span>
							<vb:if condition="$user['can_admin_ads']">
								<div class="edit-menu mainmenu module-button-item" title="{vb:phrase edit_module}"><span>{vb:phrase edit}</span>
									<ul class="submenu ui-tabs-skip">
										<li><a href="#" class="edit-button">{vb:phrase edit_module}</a></li>
										<li><a href="#" class="add-first-axd axd-edit-button" data-location="first-post">{vb:phrase add_first_post_content_ad}</a></li>
										<li><a href="#" class="add-last-axd axd-edit-button" data-location="last-post">{vb:phrase add_last_post_content_ad}</a></li>
									</ul>
								</div>
							<vb:else />
								<span class="edit-button module-button-item" title="{vb:phrase edit_module}" title="{vb:phrase edit}"><span>{vb:phrase edit}</span></span>
							</vb:if>
						</div>
					</vb:if>
					<span class="toggle-button module-button-item collapse" title="{vb:phrase collapse}" data-toggle-title="{vb:phrase expand}"><span class="vb-icon-toggle">{vb:phrase collapse}</span></span>
				</div>
				<vb:if condition="$user['userid']">
					<div class="conversation-controls right clear hide-on-editmode">
						{vb:data isSubscribed, follow, isFollowingContent, {vb:raw nodeid}}
						{vb:hook 'conversation_display_controls'}
						<button type="button" data-node-id="{vb:raw nodeid}" data-owner-id="{vb:raw currentConversation.userid}" class="is-topic button follow-btn uppercase left<vb:if condition="$isSubscribed"> isSubscribed special<vb:else /> secondary</vb:if>"><span class="button-icon"></span><span class="button-text-primary"><vb:if condition="$isSubscribed">{vb:phrase following}<vb:else />{vb:phrase follow}</vb:if></span></button>
						<vb:comment><button type="button" class="button secondary share-btn uppercase left"><span class="button-icon"></span><span class="button-text-primary">{vb:phrase share}</span></button></vb:comment>
					</div>
				</vb:if>
				<vb:if condition="!$currentNodeIsBlog">
					<div class="widget-tabs-nav clear">
						<ul class="ui-tabs-nav hide-on-editmode left">
							{vb:set extra.view, 'thread'}
							<vb:if condition="!empty($page['s'])">
								{vb:set extra.s, {vb:raw page.s}}
							</vb:if>
							<li><a href="#thread-view-tab" data-href="{vb:url {vb:raw currentConversation.routeid}, {vb:raw currentConversation}, {vb:raw extra}}">{vb:phrase posts}</a></li>
							{vb:set extra.view, 'stream'}
							<li><a href="#stream-view-tab" data-href="{vb:url {vb:raw currentConversation.routeid}, {vb:raw currentConversation}, {vb:raw extra}}">{vb:phrase latest_activity}</a></li>
						</ul>
					</div>
				</vb:if>
			</div>
			<div class="widget-content tab widget-tabs-panel">
				<input type="hidden" name="canmoderate" id="canmoderate" value="{vb:raw currentConversation.canmoderate}" />

				<div id="<vb:if condition="$page['view'] == 'stream'">stream-view-tab<vb:else />thread-view-tab</vb:if>" class="conversation-view conversation-<vb:if condition="$page['view'] == 'stream'">stream-view<vb:else />thread-view</vb:if><vb:if condition="$enableInlineMod"> inlinemod-container</vb:if>">
