code.installcode:
$vbulletin->db->hide_errors(); $vbulletin->db->query(" CREATE TABLE " . TABLE_PREFIX . "forumrunner_push_data ( id INT UNSIGNED NOT NULL AUTO_INCREMENT, vb_userid INT UNSIGNED NOT NULL DEFAULT '0', vb_pmid INT UNSIGNED NOT NULL DEFAULT '0', vb_threadid INT NOT NULL DEFAULT '0', vb_threadread INT NOT NULL DEFAULT '0', vb_subsent TINYINT UNSIGNED NOT NULL DEFAULT '0', PRIMARY KEY (id) ) ");
$vbulletin->db->query(" CREATE TABLE " . TABLE_PREFIX . "forumrunner_push_users ( id INT UNSIGNED NOT NULL AUTO_INCREMENT, vb_userid INT UNSIGNED NOT NULL, fr_username VARCHAR(45) NOT NULL, last_login DATETIME DEFAULT NULL, b TINYINT UNSIGNED NOT NULL DEFAULT '0', token TINYTEXT, PRIMARY KEY (id) ) ");
$vbulletin->db->query(" CREATE TABLE " . TABLE_PREFIX . "forumrunner_attachment ( id INT UNSIGNED NOT NULL AUTO_INCREMENT, poststarttime INT UNSIGNED NOT NULL, vb_userid INT UNSIGNED NOT NULL, filedataid INT UNSIGNED NOT NULL, attachmentid INT UNSIGNED DEFAULT NULL, PRIMARY KEY (id) ) ");
